window.onload=function(){for(var n=document.getElementsByClassName("code"),e=n.length,a=0;e>a;a++){n[a].innerHTML='<span class="line-number"></span>'+n[a].innerHTML+'<span class="cl"></span>';for(var s=n[a].innerHTML.split(/\n/).length,l=0;s>l;l++){var r=n[a].getElementsByTagName("span")[0];r.innerHTML+="<span>"+(l+1)+"</span>"}}};